var Linespin, linespin;

Linespin = require('../linespin');

linespin = new Linespin('Spinning...', 'All done!', 100);

linespin.start();

setTimeout(function() {
  return linespin.error('Error!');
}, 1000);

setTimeout(function() {
  return linespin.warn('Warning...');
}, 3000);

setTimeout(function() {
  return linespin.msg('Hello World...');
}, 3000);

setTimeout(function() {
  return linespin.pending('Pending...');
}, 4000);

setTimeout(function() {
  return linespin.success('Success!');
}, 5000);

setTimeout(function() {
  var results;
  results = 42;
  return linespin.stop("The answer is " + results);
}, 6000);
